import React, { useMemo, useRef, useState } from "react";
import { random } from "lodash";
import { useFrame } from "react-three-fiber";

export default () => {
  const esquerda = useRef();  
  const giraTriangulo = useRef()                                


  // const FakeSphere = () => {
  //   return (
  //     <mesh
  //     position={position}
  //     >       
  //       <sphereBufferGeometry args={[0.7, 30, 30]} attach="geometry" />
  //       <meshBasicMaterial color={0xfff1ef} attach="material" />

  //     </mesh>
  //   );
  // };

  useFrame(() => {
    esquerda.current.rotation.x = 0;
    esquerda.current.rotation.y = 0;
    esquerda.current.rotation.z = -1;

  });
  useFrame(() => {
    giraTriangulo.current.rotation.x = 0;
    giraTriangulo.current.rotation.y = 0.3;
    giraTriangulo.current.rotation.z = 3.19;

  });

  const FakeSphere2 = () => {
    return (
       <mesh
       ref={giraTriangulo}
       >   
         <coneBufferGeometry args={[0.7, 1, 4]} attach="geometry" /> 
        <meshBasicMaterial color="#333" attach="material" />

      </mesh>
    );
  };

  const PartedeCima = () => {
    return (
      <mesh  position={[0, 1, 0]} >       
        <boxBufferGeometry args={[4, 0.2, 0.2]} attach="geometry" />
        <meshBasicMaterial color="#333" attach="material" />

      </mesh>
    );
  };

  const ParteEsquerda = () => {
    return (
      <mesh ref={esquerda} position={[-2, 0, 0]} >       
        <boxBufferGeometry args={[4, 0.2, 0.2]} attach="geometry" />
        <meshBasicMaterial color="#333" attach="material" />

      </mesh>
    );
  };



  return (
    <group>
      {/* <FakeSphere /> */}
      <ParteEsquerda />
      <FakeSphere2 />

      <PartedeCima />
      <ambientLight intensity={0.1} />
      <pointLight intensity={1.12} position={[0, 0, 0]} />
    </group>
  );
};
