import React from "react";

export default () => {
  return (
    <div className="credits">
      <a href="https://www.twitter.com/dghez_" target="_BLANK">
        MIDAS CORPORATE
      </a>
      <style jsx>{`
        .credits {
          position: fixed;
          bottom: 30px;
          left: 30px;
          z-index: 10;
          color: white;
          font-family: 'Noto Sans JP', sans-serif;
          font-size: 12px;
          font-weight: 700;
          letter-spacing: -0.02em;
          opacity: 0.5;
          transition: all 0.6s;
        }

        .credits:hover {
          opacity: 1;
        }
      `}</style>
    </div>
  );
};
