import React from "react";

export default () => {
  return (
    <div className="credits">
      <div class="player" id="homeVideo" data-property="{videoURL:'https://youtu.be/4hi6oj5Ws4s',containment:'#home',autoPlay:true, mute:true, showControls:false, stopMovieOnBlur:false, showYTLogo: false}"></div>
      <a href="https://github.com/MidasCorporate" target="_BLANK">
        MIDAS CORPORATE
      </a>
      <style jsx>{`
        .credits {
          position: fixed;
          bottom: 30px;
          left: 30px;
          z-index: 10;
          color: white;
          font-family: 'Noto Sans JP', sans-serif;
          font-size: 12px;
          font-weight: 700;
          letter-spacing: -0.02em;
          opacity: 0.5;
          transition: all 0.6s;
        }

        .credits:hover {
          opacity: 1;
        }
        #homeVideo {
          background-color: rgba(0, 0, 0, 0.7);
          content: '';
          height: 100%;
          left: 0;
          position: absolute;
          top: 0;
          width: 100%;
          z-index: 1;
        }
      `}</style>
    </div>
  );
};
