import React, { useMemo, useRef } from "react";
import { random } from "lodash";
import { useFrame } from "react-three-fiber";

export default () => {
  const mesh = useRef();                                  
  const time = useRef(0);
  const position = useMemo(() => {
    return [0, 0.2, 0];
  }, []);                             

  const timeMod = useMemo(() => random(0.1, 4, true), []);

  const FakeSphere = () => {
    return (
      <mesh
      position={position}
      >       
        <sphereBufferGeometry args={[0.7, 30, 30]} attach="geometry" />
        <meshBasicMaterial color={0xfff1ef} attach="material" />

      </mesh>
    );
  };
  const FakeSphere2 = () => {
    return (
      <mesh>      
        <coneBufferGeometry args={[0.9, 2, 10]} attach="geometry" />
        <meshBasicMaterial color="red" attach="material" />

      </mesh>
    );
  };

  useFrame(() => {
    mesh.current.rotation.y += 0.01 * timeMod;
    if (isActiveRef.current) {
      time.current += 0.03;
      mesh.current.position.y = position[1] + Math.sin(time.current) * 0.4;
    }
  });

  const PartedeCima = () => {
    return (
      <mesh ref={mesh} position={position} >       
        <boxBufferGeometry args={[6, 0.4, 2]} attach="geometry" />
        <meshBasicMaterial color={0xfff1ef} attach="material" />

      </mesh>
    );
  };


  return (
    <group>
      {/* <FakeSphere /> */}
      <FakeSphere2 />

      <PartedeCima />
      <ambientLight intensity={0.1} />
      <pointLight intensity={1.12} position={[0, 0, 0]} />
    </group>
  );
};
