import React, { useMemo } from "react";

export default () => {
  const position = useMemo(() => {
    return [random(-3, 3, true), random(-3, 3, true), random(-3, 3, true)];
  }, []);

  const FakeSphere = () => {
    return (
      <mesh
      position={position}
      >       
        <sphereBufferGeometry args={[0.7, 30, 30]} attach="geometry" />
        <meshBasicMaterial color={0xfff1ef} attach="material" />

      </mesh>
    );
  };
  const FakeSphere2 = () => {
    return (
      <mesh>
        <sphereBufferGeometry args={[0.7, 10, 20]} attach="geometry" />
        <meshBasicMaterial color="red" attach="material" />


      </mesh>
    );
  };
  // const Position = () => {
  //   return (
  //     <group>
  //        <FakeSphere2 />
  //       <ambientLight intensity={0.3} />
  //       <pointLight intensity={1.0} position={[5, 10, 23]} />
  //     </group>
  //   )
  // }

  return (
    <group>

      <FakeSphere />

      <ambientLight intensity={0.1} />
      <pointLight intensity={1.12} position={[0, 0, 0]} />
    </group>
  );
};
