import React, { useMemo, useRef, useState } from "react";
import { random } from "lodash";
import { useFrame } from "react-three-fiber";

export default () => {
  const esquerda = useRef();                                  


  // const FakeSphere = () => {
  //   return (
  //     <mesh
  //     position={position}
  //     >       
  //       <sphereBufferGeometry args={[0.7, 30, 30]} attach="geometry" />
  //       <meshBasicMaterial color={0xfff1ef} attach="material" />

  //     </mesh>
  //   );
  // };
  const FakeSphere2 = () => {
    return (
      <mesh>      
        <coneBufferGeometry args={[0.9, 2, 10]} attach="geometry" />
        <meshBasicMaterial color="#333" attach="material" />

      </mesh>
    );
  };

  useFrame(() => {
    esquerda.current.rotation.x = 0;
    esquerda.current.rotation.y = 0;
    esquerda.current.rotation.z = -1;

  });

  const PartedeCima = () => {
    return (
      <mesh  position={[0, 0.2, 0]} >       
        <boxBufferGeometry args={[4, 0.2, 0.2]} attach="geometry" />
        <meshBasicMaterial color="#333" attach="material" />

      </mesh>
    );
  };

  const ParteEsquerda = () => {
    return (
      <mesh ref={esquerda} position={[-2, 0, 0]} >       
        <boxBufferGeometry args={[4, 0.2, 0.2]} attach="geometry" />
        <meshBasicMaterial color="#333" attach="material" />

      </mesh>
    );
  };



  return (
    <group>
      {/* <FakeSphere /> */}
      <ParteEsquerda />
      <FakeSphere2 />

      <PartedeCima />
      <ambientLight intensity={0.1} />
      <pointLight intensity={1.12} position={[0, 0, 0]} />
    </group>
  );
};
